mapper.update:
UPDATE USER_MASTER
SET PW_ERR_CNT = PW_ERR_CNT + 1
WHERE USER_ID = #{userId}

UPDATE USER_MASTER
SET PW_ERR_CNT = 0
WHERE USER_ID = #{userId}

mapper.insert:
/* 로그인 이력 쌓기 */
INSERT INTO USER_LOGIN_HISTORY(
USER_ID
,IP_ADDR
,REG_DT
) VALUES (
#{userId}
,#{ipAddr}
,now()
)

/* 회원가입 */
INSERT INTO USER_MASTER(
USER_ID
,USER_PW
,USER_NAME
,PW_ERR_CNT
,REG_DT
,MOD_DT
) VALUES (
#{userId}
,#{userPw}
,#{userName}
,0
,now()
,now()
)

mapper.select:
/* 아이디 중복 체크 */
SELECT
COUNT(*) AS CNT
FROM USER_MASTER
WHERE 1=1
AND USER_ID = #{userId}

SELECT COUNT(*)
FROM USER_FRIEND
WHERE USER_ID = #{userId} AND FRIEND_ID = #{friendId}
